<template>
  <div class="server-row mb-5">
    <div class="card" style="height: 10rem; overflow: hidden">
      <div class="row h-100">
        <div class="col-2 icon-col">
            <div class="featured-image-outer">
              <div class="featured-image-inner" :style="getImage()">
              </div>
          </div>
        </div>
        <div class="col-9">
          <h4 class="mt-3 ms-2">{{ server.name }}</h4>
          <p class="ms-2 block-truncate">{{ server.information }}</p>
        </div>

        <div class="col-1">
          <div class="icon-shape icon-md bg-light-primary text-primary rounded-1 mt-5" v-html="icon">
            {{ icon }}
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import { Server } from "../../../models";

export default {
  name: "server_card",
  data() {
    return {
      defaultImg: 'data:image/png;base64,'
    }
  },
  props: {
    server: {
      type: Server
    }
  },
  computed: {
    icon() {
      switch (this.server.type) {
        case 'minecraft':
          return '<img src="https://img.icons8.com/ios/50/000000/minecraft-creeper.png" style="width: 24px "/>';
          break;
          default:
            return '<i class="bi bi-server fs-4">'
            break;
      }
    }
  },
  methods: {
    getImage() {
      if (this.server.image) {

        return {
          background: `url('${this.server.image.replace('dataimage/pngbase64', 'data:image/png;base64,')}')`
        };
      }
      return {
        background: `url('${this.defaultImg}')`
      };
    }
  }
}
</script>

<style scoped>
.featured-image-outer {
  position: absolute;
  margin-left: -10%;
  min-height: 10rem;
  overflow: hidden;
  width: 26.5%;
  transform: skew(14deg,0deg);
}

.featured-image-inner {
  background-size: cover;
  background-position: center;
}

.featured-image-inner {
  left: 2rem;
  position: absolute;
  top: -76px;
  height: 44rem;
  width: 100%;
  transform: skew(-19deg,0deg);
}
</style>
